      } else {
        this.$router.push({ name: 'userRegisterType' })
      }
    },
    emailCheck () {
      let send = {
        email: this.vModel.email
      }
      if (send.email) {
        this.xhttp({
          url: 'user/email',
          method: 'get',
          params: send
        }).then((response) => {
          if (response.status === 200) {
            if (response.data.data === true) {
              this.style.display = 'block'
            } else {
              this.style.display = 'none'
            }
          }
          this.$Progress.finish()
        }).catch((error) => {
          this.response.emailResponse = error.response
          if (error.response.data.messages.message) {
            this.$notify('error', '오류', error.response.data.messages.message)
          } else {
            this.$notify('error', '오류', '관리자에게 문의바랍니다.')
          }
          this.$Progress.fail()
        })
      }
    },
    registerSubmit () {
      if (this.vModel.password !== this.vModel.passwordConfirm) {
        this.vModel.validation = 'on'
        this.$refs.password.focus()
      } else {
        if (this.vModel.term_check01 === false || this.vModel.term_check02 === false) {
          this.$notify('error', '약관 동의', '이용약관 및 개인정보 처리방침에 동의하세요.')
        } else {
          let send = {
            role: this.response.userType_id,
            company_name: this.vModel.company_name,
            name: this.vModel.name,
            email: this.vModel.email,
            password: this.vModel.password
          }
          this.xhttp({
            url: 'user/create',
            method: 'get',
            params: send
          }).then((response) => {
            this.response.registerResponse = response
            if (response.status === 200) {
              this.$notify('primary', '가입 완료', '가입 완료했습니다.')
              this.login(this.response.registerResponse.data)
              this.$router.push('/')
            }
            this.$Progress.finish()
          }).catch((error) => {
            if (error.response.status === 401) {
              this.$notify('error', '가입 에러', '가입에 실패했습니다.')
            }
            this.$Progress.fail()
          })
        }
      }
    }
  },
  computed: {
    ...mapGetters(['currentUser', 'processing', 'loginError']),
    passwordValidation () {
      if (this.vModel.validation === 'on') {
        return this.vModel.password.length > 5 && this.vModel.password.length < 255
      } else {
        return ''
      }
    },
    passwordConfirmValidation () {
      if (this.vModel.validation === 'on') {
        return this.vModel.password === this.vModel.passwordConfirm
      } else {
        return ''
      }
    }
  }
}
</script>
